mapper.select:
select ccr.consumable_type_id,(select name from consumable_type ct where ct.id = consumable_type_id) consumable_type_name,
ccr.consumable_item_id, (select name from consumable_item ci where ci.id = consumable_item_id) consumable_item_name,
ccr.consumable_specification_id,(select name from consumable_specification cs where cs.id = consumable_specification_id) consumable_specification_name,
ccr.create_date, sum(ccr.count)  `count`
from
(select * from consumable_consumption_record where 1=1
and consumable_type_id = #{typeId}



  and consumable_item_id = #{itemId}


  and consumable_specification_id = #{specificationId}

and create_date between #{timeBegin} and #{timeEnd}
) ccr
group by ccr.consumable_type_id, ccr.consumable_item_id, ccr.consumable_specification_id, ccr.create_date;
